  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/work/openSource/tank-websocket-go-server/docs/node_modules/.pnpm/vuepress@1.9.10_@vue+compil_b9c608fd952d8600dd545c097923d33e/node_modules/vuepress/node_modules:/mnt/d/work/openSource/tank-websocket-go-server/docs/node_modules/.pnpm/vuepress@1.9.10_@vue+compil_b9c608fd952d8600dd545c097923d33e/node_modules:/mnt/d/work/openSource/tank-websocket-go-server/docs/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
